con:mockService.con:startScript:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def scriptPath = groovyUtils.projectPath + "/groovy"
def dataPath = groovyUtils.projectPath + "/data"
GroovyScriptEngine engine = new GroovyScriptEngine(scriptPath)
def utilsScript = engine.loadScriptByName("Utils.groovy")
// create a runtime instance of teh script
utils = utilsScript.newInstance()
// put teh instance in the context
context.utils = utils

def orderInquiryScript = engine.loadScriptByName("OrderInquiry.groovy")
// create a runtime instance of the script
orderInquiry = orderInquiryScript.newInstance()
context.orders = orderInquiry.loadOrders(dataPath)
context.orderInquiryScript = orderInquiryScript

con:mockService.con:stopScript:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def scriptPath = groovyUtils.projectPath + "/groovy"
def dataPath = groovyUtils.projectPath + "/data"
log.info "[stopScript] - path: $dataPath"

GroovyScriptEngine engine = new GroovyScriptEngine(scriptPath)
def orderInquiryScript = engine.loadScriptByName("OrderInquiry.groovy")
// create a runtime instance of the script
orderInquiry = orderInquiryScript.newInstance()
orderInquiry.saveOrders(dataPath, context.orders)

con:mockOperation.con:dispatchPath:
import java.text.SimpleDateFormat

// create XmlHolder for request content
//def holder = new com.eviware.soapui.support.XmlHolder( mockRequest.requestContent )
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(mockRequest.requestContent)
holder.declareNamespace( 'ord', 'http://www.pluralsight.com/service/Order')

// get nodes
def nodeOrderId = holder.getDomNode("//ord:uniqueOrderId")
def nodeEan13 = holder.getDomNode("//ord:ean13")
def nodeAccountId = holder.getDomNode("//ord:accountId")
def nodeQuantity = holder.getDomNode("//ord:orderQuantity")

//return "RESP-default"
if (nodeOrderId == null)
	return "RESP-NO-OrderID"
if (nodeAccountId == null)
	return "RESP-NO-AccountID"
if (nodeEan13 == null) {
	return "RESP-NO-OrderID"
}
Long ean13 = Long.parseLong(holder["//ord:ean13"]) // or holder.getNodeValue("//ord:ean13")
log.info("Ean: " + ean13)
if (1L.equals(ean13))
	return "RESP-StopMock"
if (999L.equals(ean13))
	return "RESP-Ping"

Long orderId = Long.parseLong(holder["//ord:uniqueOrderId"])
Long accountId = Long.parseLong(holder["//ord:accountId"])
Integer quantity = 1
if (nodeQuantity != null)
	quantity = Integer.parseInt(holder["//ord:orderQuantity"])
orderInquiry = context.orders.get(accountId)
if (orderInquiry == null) {
	orderInquiry = context.orderInquiryScript.newInstance(accountId)
	List<Object> items = new ArrayList<>()
	items.add(orderInquiry.createItem(1L, new Date(), ean13, "My Book", 12, quantity))
	orderInquiry.addOrder("READY", items)
	context.orders.put(accountId, orderInquiry)
	requestContext.accountId = accountId
	requestContext.ean131 = ean13
	requestContext.tile1 = "My Book"
	requestContext.lineNr1 = "1"
	requestContext.shipDate1 = new SimpleDateFormat("yyyy-M-dd").format(new Date())
	requestContext.price1 = "12"
	requestContext.quantity1 = quantity
	return "RESP-Orders1"
} else if (orderInquiry.orders.size() < 2) {
	List<Object> items = new ArrayList<>()
	items.add(orderInquiry.createItem(2L, new Date(), ean13, "My Second Book", 14, quantity))
	orderInquiry.addOrder("READY", items)
	def firstItem = orderInquiry.orders.get(0).items.get(0)
	context.orders.put(accountId, orderInquiry)
	requestContext.accountId = accountId
	requestContext.ean131 = firstItem.ean13
	requestContext.tile1 = firstItem.title
	requestContext.lineNr1 = "1"
	requestContext.shipDate1 = new SimpleDateFormat("yyyy-M-dd").format(firstItem.shippingDate)
	requestContext.price1 = firstItem.price
	requestContext.quantity1 = firstItem.quantity
	requestContext.ean132 = ean13
	requestContext.tile2 = "My Second Book"
	requestContext.lineNr2 = "2"
	requestContext.shipDate2 = new SimpleDateFormat("yyyy-M-dd").format(new Date())
	requestContext.price1 = "14"
	requestContext.quantity1 = quantity
	return "RESP-Orders2"
}
